mapper.select:
SELECT id, name, description, brand_name, price, unit, volume, dimension, package_type, origin, expiration, price_per_100g,
       allergic_info, model_serial_no, rating, search_keyword, stock, discount_price, author, publisher, isbn, published_time, oneday_eligible, fresh_eligible
FROM ITEM WHERE id = #{id}

mapper.update:
UPDATE ITEM SET
    name = #{name},
    description = #{description},
    price = #{price}
WHERE id = #{id}
